<?php
/**
 * Provide a admin requirement view for the plugin
 *
 * @since      1.0.0
 *
 * @package    wc-spod
 * @subpackage wc-spod/admin/partials
 */
$server_phpversion = phpversion();
$server_php_memory_usage = round(memory_get_usage()/1024/1024);
$server_php_memory_limit = ini_get('memory_limit');

$software_wordpress_version = get_bloginfo( 'version' );
$software_woocommerce_version = WC_VERSION;
?>
<div class="ng-box-wrapper">
    <div class="box settings">
        <div class="box__logo">
            <img src="data:image/svg+xml;base64," alt="spod logo" title="spod logo">
        </div>
        <div class="box__headline">
            <h1><?php _e('Plugin Requirements','wc-spod'); ?></h1>
        </div>

       <div class="box__form">
            <h2 class="title"><?php _e('Server','wc-spod'); ?></h2>
            <table class="wc_status_table widefat" id="status" cellspacing="0">
                <thead>
                    <tr>
                        <th><?php _e('Service','wc-spod'); ?></th>
                        <th><?php _e('Value','wc-spod'); ?></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td><?php _e('PHP Version'); ?></td>
                        <td class="">
                            <span class="wp-setting <?php echo (version_compare( $server_phpversion, MIN_PHP_VERSION_REQUIRED, '<=' ) ? 'error' : 'success'); ?>"><?php echo phpversion(); ?></span>
                            <span class="plugin-notice"><?php _e('This plugin requires at least PHP7.3 and is tested up to PHP8.0'); ?></span>
                        </td>
                    </tr>
                    <tr>
                        <td><?php _e('PHP Memory Usage'); ?></td>
                        <td class="">
                            <span class="wp-setting <?php echo ($server_php_memory_usage < 10 ? 'error' : 'success'); ?>"><?php echo $server_php_memory_usage; ?>MB</span>
                            <span class="plugin-notice"><?php _e('This plugin requires at least 10MB to sync data between SPOD and WooCommerce'); ?></span>
                        </td>
                    </tr>
                    <tr>
                        <td><?php _e('PHP Memory Limit'); ?></td>
                        <td class="">
                            <span class="wp-setting"><?php echo $server_php_memory_limit; ?></span>
                            <span class="plugin-notice"><?php _e('This plugin requires a minimal memory limit of 64MB'); ?></span>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
        <div class="box__form">
            <h2 class="title"><?php _e('Software','wc-spod'); ?></h2>
            <table class="wc_status_table widefat" id="status" cellspacing="0">
                <thead>
                <tr>
                    <th><?php _e('Service','wc-spod'); ?></th>
                    <th><?php _e('Value','wc-spod'); ?></th>
                </tr>
                </thead>
                <tbody>
                    <tr>
                        <td><?php _e('Wordpress Version'); ?></td>
                        <td>
                            <span class="wp-setting <?php echo (version_compare( $software_wordpress_version, MIN_WORDPRESS_VERSION_REQUIRED, '<=' ) ? 'error' : 'success'); ?>"><?php echo $software_wordpress_version; ?></span>
                            <span class="plugin-notice"><?php _e('This plugin requires at least WordPress '); ?> <?php echo MIN_WORDPRESS_VERSION_REQUIRED; ?></span>
                        </td>
                    </tr>
                    <tr>
                        <td><?php _e('SPOD Plugin Version'); ?></td>
                        <td>
                            <span class="wp-setting"><?php echo SPOD_POD_VERSION; ?></span>
                            <span class="plugin-notice"><?php _e('Your SPOD Plugin Version'); ?></span>
                        </td>
                    </tr>
                    <tr>
                        <td><?php _e('Woocommerce Version'); ?></td>
                        <td>
                            <span class="wp-setting <?php echo (version_compare( $software_woocommerce_version, MIN_WOOCOMMERCE_VERSION_REQUIRED, '<=' ) ? 'error' : 'success'); ?>"><?php echo $software_woocommerce_version; ?></span>
                            <span class="plugin-notice"><?php _e('This plugin requires at least WooCommerce '); ?> <?php echo MIN_WOOCOMMERCE_VERSION_REQUIRED; ?></span>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
</div>